import React, { useState, useEffect, useRef, useContext } from 'react'
import AttachFileIcon from '@material-ui/icons/AttachFile';
import IconButton from '@material-ui/core/IconButton';
import SendIcon from '@material-ui/icons/Send';
import { UserContext } from '../UserProvider';
import { postFormData, postMsgData, getExternalData, postData } from '../services/NetworkService';
import { io } from "socket.io-client";
import { useTranslation } from 'react-i18next'

const ENDPOINT = process.env.REACT_APP_CHAT_URL

function ChatUi({ orderData }) {
    const [show, setShow] = useState(false)
    const messagesEndRef = useRef(null)
    const { user } = useContext(UserContext)
    const [file, setFile] = useState(null);
    const socket = io(ENDPOINT)
    const [messages, setMessages] = useState(null)
    const [message, setMessage] = useState(null)
    const [text, setText] = useState("")
    const { t } = useTranslation();

    socket.on('message', (data) => {
        console.log('message from server')
        //        if (data.user !== user.name){
        setMessage(data)
        //        }
    })
    useEffect(() => {
        if (message) {
            console.log(message)
            setMessages([...messages, message])
            console.log("chatui")
        }
    }, [message, setMessages])

    const scrollToBottom = () => {
        if (show) {
            messagesEndRef.current.scrollIntoView({ behavior: "smooth" })
        }
    }
    useEffect(scrollToBottom, [messages, show]);
    useEffect(() => {
        if (orderData) {
            socket.emit('join', { user: orderData.createdBy, receiver: 'helpdesk', identifier: orderData.id })
            getExternalData(ENDPOINT + "/api/v1/messages?user=" + user.name + '&id=' + orderData.id)
                .then(res => {
                    if (res.data) {
                        setMessages(res.data)
                    }
                })
            console.log("messages", messages)
        }
        return () => socket.disconnect();

    }, [orderData, setMessages])

    const handleSubmit = (e) => {
        e.preventDefault()
        let dataFiles = [];
        let artifect_types = '';
        let base_paths = '';
        if (file) {
            dataFiles.push(file)
            artifect_types += 'attatchment'
            var formData = new FormData();
            formData.append('artifect_type', artifect_types)
            artifect_types.split(',').map(type => {
                base_paths += '/chats/' + orderData.id + '/' + type + ","
            })
            base_paths = base_paths.slice(0, -1);
            formData.append('base_path', base_paths)
            dataFiles.map(o => {
                formData.append('dataFiles', o)
            })
            postFormData(formData).then(res => {
                if (res.data.files) {
                    const receiver = 'helpdesk'
                    const msg = { user: user.name, receiver: receiver, message: res.data.files[0].path, identifier: orderData.id, isMedia: true }
                    postMsgData(ENDPOINT + "/api/v1/messages", msg).then(o => {
                        setFile(null)
                        if (o.status == 200) {
                            socket.emit('message', o.data)
                            //                            setMessages([...messages, o.data])
                            postNotification(orderData, t('chat attachment notification', {name: res.data.files[0].filename}))
                        }
                    })
                }
            });
        }
        else {
            const receiver = 'helpdesk'
            const msg = { user: user.name, receiver: receiver, message: text, identifier: orderData.id }
            postMsgData(ENDPOINT + "/api/v1/messages", msg).then(o => {
                setText("")
                if (o.status == 200) {
                    socket.emit('message', o.data)
                    //                    setMessages([...messages, o.data])
                    postNotification(orderData, o.data.message)
                }
            })
        }
    }

    const postNotification = (o, message_) => {
        let title = t('chat notification', {company: o.client?.society?.toUpperCase()})
        let notification = {type: "CHAT", orderId: o.id, title: title, message: message_, link: `/orders/${o.id}?chat`}
        postData("notifications", notification).then(res => console.log(res))
    }

    return (

        <div className="rcw-widget-container">
            <div className={show ? "rcw-conversation-container active" : "rcw-conversation-container hidden"}
                //  style={!show?{display:"none"}:{}}
                aria-live="polite">
                {show && <>
                    <div className="rcw-header">
                        <h4 className="rcw-title">Helpdesk</h4>
                        <span></span>
                    </div>
                    <div id="messages" className="rcw-messages-container">
                        {messages && messages.map((msg, index) => (
                            <div className="rcw-message" key={index}>
                                <div className={(msg.user === user.name) ? "rcw-client" : "rcw-response"}>
                                    <div className="rcw-message-text">
                                        {!msg.isMedia ? <p>{(msg.user === user.name) ? msg.message : msg.user + " : " + msg.message}</p>
                                            : <a href={msg.message} target="_blank">
                                                {/* <img src={msg.message}/> */}
                                                {msg.message.split("-")[1]}</a>}
                                    </div>
                                    <span className="rcw-timestamp">{msg.timestamp && msg.timestamp.slice(11, 16)}</span>
                                </div>
                            </div>
                        ))}
                        <div ref={messagesEndRef} />
                    </div>
                    {/* <div className={show ? "loader" : "loader active"}>
                    <div className="loader-container">
                        <span className="loader-dots"></span>
                        <span className="loader-dots"></span>
                        <span className="loader-dots"></span>
                    </div>
                </div> */}
                    {file &&
                        <div className="rcw-file">
                            <AttachFileIcon color="disabled" fontSize="small" />
                            <p style={{ overflowWrap: "break-word", minWidth: "0px", height: 2, fontWeight: 20 }}
                            >{file.name}</p>
                        </div>
                    }
                    <form className="rcw-sender" onSubmit={handleSubmit}>
                        <input
                            type="text"
                            className="rcw-new-message expand"
                            name="message"
                            value={text}
                            onChange={(e) => setText(e.target.value)}
                            placeholder="Type a message..."
                            autoComplete="off" />
                        <input
                            id="icon-button-file"
                            type="file"
                            onChange={(e) => setFile(e.target.files[0])}
                            style={{ display: 'none' }} />
                        <label htmlFor="icon-button-file">
                            <IconButton color="primary" aria-label="upload picture"
                                component="span">
                                <AttachFileIcon color="disabled" fontSize="small" />
                            </IconButton>
                        </label>
                        <IconButton color="primary" aria-label="upload picture" type="submit"  >
                            <SendIcon color="disabled" fontSize="small" />
                        </IconButton>
                    </form>
                </>}
            </div>
            <button type="button" className={show ? "rcw-launcher rcw-hide-sm" : "rcw-launcher "} aria-controls="rcw-chat-container" onClick={() => setShow(!show)}>
                <img src={show ?
                    "data:image/svg+xml;base64,"
                    :
                    "data:image/svg+xml;base64,"}
                    className={show ? " rcw-close-launcher" : "rcw-open-launcher"} alt="chat" />
            </button>
        </div >

    )
}
export default ChatUi